export function lazyLoadImages(){const e=document.querySelectorAll("img[data-src]");if(!e.length)return;const t=new IntersectionObserver((e=>{for(const n of e)if(n.isIntersecting){const e=n.target;if(e instanceof HTMLImageElement){const n=e.dataset.src;n&&(e.src=n,t.unobserve(e))}}}),{rootMargin:"0px 0px 50px 0px",threshold:.1});for(const n of e)t.observe(n);return()=>{t.disconnect()}}